% April 3, 2020% reportGraph.m used with assign08.mfunction [] = reportGraphM( date, scores, loc, opp )% print AU softball report and graph scores% get size of stats[ nGames, nStats ] = size( date );% get season record info -- LOOPS NOT ALLOWEDauWins = length( find( scores(:,1) > scores(:,2 ) ) );[ maxSpread, spGame ] = max( abs( scores(:,1) - scores(:,2) ) );[ maxAU,auGame ] = max( scores(:,1) );[ maxOpp,oppGame ] = max( scores(:,2) );% print season report: date, scores, batting average% print title and headersfprintf( '2020 AU Softball Results as of %02d/%02d\n', date(nGames,:) )fprintf( 'Date Opponent Score W/L \n' )for g = 1:nGames    if strfind( loc{g}, 'home' )        fprintf( '%02d/%02d %-16s %02d-%02d ', date(g,:), opp{g}, scores(g,:) )        if scores(g,1) > scores(g,2)            fprintf( 'W\n' )        else            fprintf( 'L\n' )        end    endend% season recordfprintf( '\nSeason record, all games: \n' )fprintf( 'Wins-Losses: %d-%d\n', auWins, nGames-auWins )fprintf( 'Largest pt spread: %2d on %02d/%02d \n', maxSpread, date(spGame,1), date(spGame,2) )fprintf( 'Most AU runs: %2d on %02d/%02d \n', maxAU, date(auGame,1), date(auGame,2) )fprintf( 'Most Opp runs: %2d on %02d/%02d \n', maxOpp, date(oppGame,1), date(oppGame,2) )% plot Auburn and opponents scores using stacked bar graphgraphScores( scores )endfunction [] = graphScores( scores )    % plot Auburn and opponents scores in separate plots    gamesNum = 1:length( scores(:,1) );    subplot( 2,1,1 )    plot( gamesNum, scores(:,1), 'r:o' )    title( '2020 Auburn Softball Scores' )    xlabel( 'Game number' )    ylabel( 'Scores' )    subplot( 2,1,2 )    plot( gamesNum, scores(:,2), 'k-s' )    title( '2020 Opponent Softball Scores' )    xlabel( 'Game number' )    ylabel( 'Scores' )end